import { Image, Text, TouchableOpacity, View, StyleSheet, TextInput, } from "react-native";
import React, { useState } from "react";

const LuasJG = ({ navigation }) => {
    const [luasJG, ubahLuas] = useState(0)
    const [alasJG, ubahAlas] = useState(0)
    const [tinggiJG, ubahTinggi] = useState(0)
    const hasilKali = () => {
        ubahLuas(parseInt(alasJG) * parseInt(tinggiJG))
    };
    return (
        <View style={styles.container}>
            <Image source={require("./assets/Jajargenjang.png")} style={styles.image} />
            <Text style={styles.JGText}>Luas Jajar Genjang</Text>
            <View style={styles.subCont}>
                <Text style={styles.AlasText}>Alas:</Text>
                <View>
                    <TextInput onChangeText={ubahAlas} placeholderTextColor='#CCD5CB' keyboardType="number-pad" placeholder="Masukkan Alas Jajar Genjang" style={styles.Alas}></TextInput>
                </View>
                <Text style={styles.TinggiText}>Tinggi:</Text>
                <View>
                    <TextInput onChangeText={ubahTinggi} placeholderTextColor='#CCD5CB' keyboardType="number-pad" placeholder="Masukkan Tinggi Jajar Genjang" style={styles.Tinggi}></TextInput>
                </View>
                <TouchableOpacity
                    onPress={hasilKali}
                    style={styles.HasilButton}
                >
                    <Text style={styles.Hasil}>Hasil</Text>
                </TouchableOpacity>
                <Text style={styles.HasilText}>Luas Jajar Genjangnya adalah = {luasJG}</Text>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#00101D",
        alignItems: "center",
    },
    subCont: {
        marginTop: 85,
    },
    JGText: {
        fontSize: 20,
        fontWeight: 'bold',
        top: 100,
        left: 4,
        color: "#FFFFFF",
        textAlign: "center",
    },
    AlasText: {
        fontSize: 25,
        fontWeight: 'bold',
        top: 153,
        right: 139,
        color: "#FFFFFF",
        textAlign: "center",
    },
    Alas: {
        width: 241,
        height: 37,
        backgroundColor: "#1A4E55",
        borderRadius: 20,
        justifyContent: "center",
        alignItems: "center",
        top: 120,
        left: 80,
        textAlign: 'center',
    },
    TinggiText: {
        fontSize: 25,
        fontWeight: 'bold',
        top: 153,
        right: 140,
        color: "#FFFFFF",
        textAlign: "center",
    },
    Tinggi: {
        width: 241,
        height: 37,
        backgroundColor: "#1A4E55",
        borderRadius: 20,
        justifyContent: "center",
        alignItems: "center",
        top: 120,
        left: 80,
        textAlign: 'center',
    },
    image: {
        width: 229,
        height: 99,
        top: 103,
    },
    HasilButton: {
        width: 83,
        height: 36,
        top: 143,
        left: 150,
        border: "1px solid #000",
        borderRadius: 10,
        backgroundColor: "#1A4E55",
        boxShadow: "0px 4px 4px 0px rgba(0, 0, 0, 0.60)",
    },
    Hasil: {
        paddingTop: 3,
        fontSize: 20,
        left: 1,
        // fontFamily: 'Squada One',
        color: "#FFFFFF",
        textAlign: "center",
    },
    HasilText: {
        fontSize: 20,
        fontWeight: 'bold',
        top: 163,
        right: 5,
        color: "#FFFFFF",
        textAlign: "center",
    },
});

export default LuasJG;
