import React from 'react';
import { View, Text, Image, TextInput, Button, TouchableOpacity, StyleSheet } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.welcome}>
        <Text style={styles.FirstText}>Where do you want to go first?</Text>
      </TouchableOpacity>
        <Text style={styles.click}>Click the button</Text>
      <Image source={require("./assets/GMALogo.png")} style={styles.image} />
      <View style={styles.Memberbutton}>
        <TouchableOpacity style={styles.TouchableOpacity}>
          <Text style={styles.ListMember}>List Member</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.Gunungbutton}>
          <Text style={styles.ListGunung}>List Gunung</Text>
        </TouchableOpacity>
        {/* <View style={styles.iconContainer}>
          <Image style={styles.google} source={require("./assets/google.png")} />
          <Image style={styles.facebook} source={require("./assets/facebook.png")} />
          <Image style={styles.apple} source={require("./assets/apple.png")} />
        </View> */}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
    alignItems: "center",
  },
  image: {
    width: 266,
    height: 266,
  },
  FirstText: {
    width: 399,
    height: 34,
    // fontFamily: 'Squada One',
    // fontWeight: 400,
    color: '#000000',
    textAlign: 'center',
    position: 'absolute',
    top: 250,
    left: -197,
    fontSize: 25,
  },
  click: {
    color: '#000000',
    textAlign: 'center',
    top: 425,
    right: 68,
    fontSize: 21,
  },
  // Memberbutton: {
  //   width: 260,
  //   height: 234,
  //   backgroundColor: '#FCF8F8',
  //   marginTop: 20,
  //   borderRadius: 20,
  // },
  TouchableOpacity: {
    width: 300,
    height: 80,
    backgroundColor: "#d9d9d9",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
    marginLeft: 5,
    marginTop: 170,
    borderColor: '#007BFF',
    borderWidth: 2,
  },
  ListMember: {
    color: "#007BFF",
    fontWeight: "600",
    fontSize: 22,
  },
  Gunungbutton: {
    width: 300,
    height: 80,
    backgroundColor: '#d9d9d9',
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft: 5,
    marginTop: 23,
    borderWidth: 2,
    borderColor: 'red', 
  },
  ListGunung: {
    color: '#ff0606',
    fontWeight: "600",
    fontSize: 22,
  },
  
