import React from 'react';
import { View, Text, Image, TextInput, Button, TouchableOpacity, StyleSheet } from 'react-native';
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

import RegisterScreen from './RegisterScreen';
import LoginScreen from './LoginScreen';

const Stack = createStackNavigator();

const App = () => {
    return (
        <View style={styles.container}>
            <Image source={require("./assets/GMALogo.png")} style={styles.image} />
            <View style={styles.loginbutton}>
                <TouchableOpacity
                 style={styles.LoginText}>
                    <Text style={styles.login}>Login</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={() => navigation.navigate('RegisterScreen')}
                style={styles.Registerbutton}>
                    <Text style={styles.register}>Register</Text>
                </TouchableOpacity>
            </View>

            <NavigationContainer>
              <Stack.Navigator intialRoutename="App">
                <Stack.Screen name="RegisterScreen" component={RegisterScreen} options={{headerShown:false}}/>
              </Stack.Navigator>
            </NavigationContainer>

            <NavigationContainer>
{/* inisialisasi tumpukan navigator dengan rute awal yang ditentukan sebagai Home  */}
        <Stack.Navigator initialRouteName="SignScreen">
            {/* setiap stack.screen merepresentasikan satu rute (layar) dalam tumpukan navigator.
            Anda dapat menambahkan lebih banyak stack.screen sesuai dengan kebutuhan aplikasi. */}
            {/* contoh */}
            {/* stack.screen name=namarute  component={namarute } */}
            {/* layar home akan menggunakan komponen homescreen untuk tampilan nya */}
            <Stack.Screen name="Re" component={LoginScreen} options={{headerShown:false}} />
            {/* layar detail akan menggunakan komponen detailscreen untuk tampilan nya */}
        </Stack.Navigator>
    </NavigationContainer>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#FFFFFF',
        alignItems: "center",
    },
    image: {
        width: 393,
        height: 349,
    },
    loginbutton: {
        // width: 313,
        // height: 86,
        // borderRadius: 10,
        // border: '1px solid #000',
        // backgroundColor: '#007BFF',
        // boxShadow: '0px 4px 4px 0px rgba(0, 0, 0, 0.60)',
    },
    LoginText: {
        // paddingTop: 20,
        // // justifyContent: 'center',
        // // textAlign: 'center',
        // // alignItems: 'center',
        width: 313,
        height: 86,
        marginTop: 83,
        border: '1px solid #000',
        borderRadius: 10,
        backgroundColor: '#007BFF',
        boxShadow: '0px 4px 4px 0px rgba(0, 0, 0, 0.60)',
    },
    login: {
        // fontSize: 32,
        // fontFamily: 'Squada One',
        // color: '#FFFFFF',
        // textAlign: 'center',
        // // alignItems: 'center',
        // // justifyContent: 'center',
        paddingTop: 20,
        fontSize: 32,
        fontFamily: 'Squada One',
        color: '#FFFFFF',
        textAlign: 'center',
    },
    Registerbutton: {
        width: 313,
        height: 86,
        marginTop: 83,
        border: '1px solid #000',
        borderRadius: 10,
        backgroundColor: '#4CAF50',
        boxShadow: '0px 4px 4px 0px rgba(0, 0, 0, 0.60)',
        borderWidth: 1,
        borderColor: 'black',
    },
    register: {
        paddingTop: 20,
        fontSize: 32,
        fontFamily: 'Squada One',
        color: '#FFFFFF',
        textAlign: 'center',
    }
});

export default App;
